$(function() {
    <? echo $plan['etype'] ? "range_div('{$plan['etype']}')" : "range_div('0')" ?>
    
    $('#etype').change(function(){
         range_div($('#etype').val())
    });
});
function range_div(va) {
    if(va == '0') {
        $('#percent').show(); 
        $('#range').hide();
        $('.faucet').hide();
        $('.faucet_auto').hide();
        $('.faucet_m').hide();
        $('.rest').show();
        $('.min').hide();
        $('.maxfield').attr("placeholder", "Percent");
        $("#limit_currency").attr('required', false);
        $("#accept_account_balance").attr('checked', true);
         $("#accept_processings").attr('checked', true);
    } else if(va == '1' || va == '2') {
        $('#percent').hide();
        $('.faucet').hide();
        $('.faucet_auto').hide();
        $('.faucet_m').hide();
        $('.rest').show();
        $('#range').show();
        $('.min').show();
        $('.max').show();
        $('.maxfield').attr("placeholder", "Max Percent");
        $("#limit_currency").attr('required', false);
        $("#accept_account_balance").attr('checked', true);
        $("#accept_processings").attr('checked', true);
    }else if(va == '3') {
        $('#percent').hide(); 
        $('#range').hide();
        $('.faucet').hide();
        $('.faucet_auto').hide();
        $('.faucet_m').hide();
        $('.rest').show();
        $('.min').hide();
        $('.max').hide();
        $("#limit_currency").attr('required', false);
    }else if(va == '4') {
        $('.rest').hide();
        $('.faucet').show();
        $('.faucet_auto').show();
        $("#limit_currency").attr('required', true);
    } else {
        $('#percent').show(); 
        $('#range').hide();
        $('.faucet').hide();
        $('.faucet_auto').hide();
        $('.faucet_m').hide();
        $('.rest').show();
        $('.min').hide();
        $('.maxfield').attr("placeholder", "Percent");
        $("#limit_currency").attr('required', false);
        $("#accept_account_balance").attr('checked', true);
        $("#accept_processings").attr('checked', true);
    }
}
</script>

