      $interval = $diff->format("%d days %h hours %i minutes");
      $interval = preg_replace('/(^0| 0) (days|hours|minutes)/', '', $interval);
      $de['expiry'] = date("Y-m-d H:i:s", addDays($de['datetime'], $package['duration'], $package['diff_in_seconds'], $exclude_days));
