function insertImageIntoQuill(imageUrl, imageAlt, imageTitle) {
    const range = quill.getSelection();
    if (range) {
        quill.insertEmbed(range.index, 'image', imageUrl);
        quill.setSelection(range.index + 1);
    }
}


